import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [category, setCategory] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [filterCategory, setFilterCategory] = useState('');

  const fetchTasks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/tasks');
      setTasks(res.data);
    } catch (err) {
      console.error("Error fetching tasks:", err.message);
    }
  };

  const addTask = async () => {
    if (title.trim() && description.trim() && dueDate.trim() && category.trim()) {
      try {
        const res = await axios.post('http://localhost:5000/api/tasks', {
          title, description, dueDate, category
        });
        setTasks([...tasks, res.data]);
        setTitle('');
        setDescription('');
        setDueDate('');
        setCategory('');
      } catch (err) {
        console.error("Error adding task:", err.message);
      }
    }
  };

  const deleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/tasks/${id}`);
      setTasks(tasks.filter(task => task._id !== id));
    } catch (err) {
      console.error("Error deleting task:", err.message);
    }
  };

  const toggleTask = async (id, completed) => {
    try {
      const res = await axios.put(`http://localhost:5000/api/tasks/${id}`, { completed: !completed });
      setTasks(tasks.map(t => (t._id === id ? res.data : t)));
    } catch (err) {
      console.error("Error updating task:", err.message);
    }
  };

  const startEdit = (id, title, description, dueDate, category) => {
    setTasks(tasks.map(task =>
      task._id === id ? {
        ...task,
        isEditing: true,
        editTitle: title,
        editDescription: description,
        editDueDate: dueDate,
        editCategory: category
      } : task
    ));
  };

  const saveEdit = async (id) => {
    const taskToUpdate = tasks.find(t => t._id === id);
    try {
      const res = await axios.put(`http://localhost:5000/api/tasks/${id}`, {
        title: taskToUpdate.editTitle,
        description: taskToUpdate.editDescription,
        dueDate: taskToUpdate.editDueDate,
        category: taskToUpdate.editCategory,
      });
      setTasks(tasks.map(t =>
        t._id === id ? { ...res.data, isEditing: false } : t
      ));
    } catch (err) {
      console.error("Error editing task:", err.message);
    }
  };

  const filteredTasks = tasks.filter((task) => {
    const titleMatch = task?.title?.toLowerCase().includes(searchQuery.toLowerCase()) || false;
    const descMatch = task?.description?.toLowerCase().includes(searchQuery.toLowerCase()) || false;
    return (titleMatch || descMatch) && (filterCategory ? task.category === filterCategory : true);
  });

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-4 sm:p-6">
      <div className="max-w-3xl mx-auto bg-white shadow-md rounded-xl p-6">
        <h1 className="text-4xl font-bold mb-6 text-center text-blue-700">üìù Task Mate</h1>

        {/* Search + Filter */}
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-6">
          <input
            type="text"
            placeholder="üîç Search tasks..."
            className="border border-gray-300 p-2 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <select
            className="border border-gray-300 p-2 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400"
            value={filterCategory}
            onChange={(e) => setFilterCategory(e.target.value)}
          >
            <option value="">üìÇ All Categories</option>
            <option value="Work">üíº Work</option>
            <option value="Personal">üè° Personal</option>
            <option value="Shopping">üõí Shopping</option>
          </select>
        </div>

        {/* Add Task */}
        <div className="grid sm:grid-cols-5 gap-2 mb-6">
          <input
            type="text"
            placeholder="Title"
            className="border p-2 rounded"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <input
            type="text"
            placeholder="Description"
            className="border p-2 rounded"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <input
            type="date"
            className="border p-2 rounded"
            value={dueDate}
            onChange={(e) => setDueDate(e.target.value)}
          />
          <select
            className="border p-2 rounded"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            <option value="">Category</option>
            <option value="Work">Work</option>
            <option value="Personal">Personal</option>
            <option value="Shopping">Shopping</option>
          </select>
          <button
            onClick={addTask}
            className="bg-blue-600 hover:bg-blue-700 text-white rounded p-2 transition"
          >
            ‚ûï Add
          </button>
        </div>

        {/* Task List */}
        <ul className="space-y-4">
          {filteredTasks.map((task) => (
            <li
              key={task._id}
              className={`p-4 rounded-xl shadow-sm flex justify-between items-start bg-white hover:bg-gray-50 transition ${
                task.completed ? "bg-gray-200 line-through text-gray-500" : ""
              }`}
            >
              <div className="flex-1 space-y-1">
                <div className="flex items-start gap-3">
                  <input
                    type="checkbox"
                    checked={task.completed}
                    onChange={() => toggleTask(task._id, task.completed)}
                    className="mt-1"
                  />
                  {task.isEditing ? (
                    <div className="flex flex-col sm:flex-row gap-2 flex-wrap w-full">
                      <input
                        type="text"
                        className="border px-2 py-1 rounded"
                        value={task.editTitle}
                        onChange={(e) =>
                          setTasks(tasks.map(t =>
                            t._id === task._id ? { ...t, editTitle: e.target.value } : t
                          ))
                        }
                      />
                      <input
                        type="text"
                        className="border px-2 py-1 rounded"
                        value={task.editDescription}
                        onChange={(e) =>
                          setTasks(tasks.map(t =>
                            t._id === task._id ? { ...t, editDescription: e.target.value } : t
                          ))
                        }
                      />
                      <input
                        type="date"
                        className="border px-2 py-1 rounded"
                        value={task.editDueDate}
                        onChange={(e) =>
                          setTasks(tasks.map(t =>
                            t._id === task._id ? { ...t, editDueDate: e.target.value } : t
                          ))
                        }
                      />
                      <select
                        className="border px-2 py-1 rounded"
                        value={task.editCategory}
                        onChange={(e) =>
                          setTasks(tasks.map(t =>
                            t._id === task._id ? { ...t, editCategory: e.target.value } : t
                          ))
                        }
                      >
                        <option value="Work">Work</option>
                        <option value="Personal">Personal</option>
                        <option value="Shopping">Shopping</option>
                      </select>
                    </div>
                  ) : (
                    <div className="space-y-1">
                      <div className="font-semibold text-lg">{task.title}</div>
                      <div className="text-sm text-gray-600">{task.description}</div>
                      <div className="text-xs text-gray-400">
                        Due: {task.dueDate} | Category: {task.category}
                      </div>
                    </div>
                  )}
                </div>
              </div>
              <div className="flex flex-col gap-2 ml-4">
                {task.isEditing ? (
                  <button onClick={() => saveEdit(task._id)} className="text-green-600 hover:text-green-800 text-lg">‚úÖ</button>
                ) : (
                  <button onClick={() => startEdit(task._id, task.title, task.description, task.dueDate, task.category)} className="text-yellow-500 hover:text-yellow-700 text-lg">‚úè</button>
                )}
                <button onClick={() => deleteTask(task._id)} className="text-red-500 hover:text-red-700 text-lg">üóë</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
  {/* Animated Background */}
<div className="absolute top-0 left-0 w-full h-full overflow-hidden -z-10">
  <div className="absolute w-72 h-72 bg-blue-200 rounded-full mix-blend-multiply filter blur-2xl opacity-50 animate-float" style={{ top: '-5rem', left: '-5rem' }}></div>
  <div className="absolute w-72 h-72 bg-purple-200 rounded-full mix-blend-multiply filter blur-2xl opacity-50 animate-float-delay" style={{ bottom: '-5rem', right: '-5rem' }}></div>
</div>

}

