import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './index.css'; // Make sure you have animation styles here

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [category, setCategory] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [filterCategory, setFilterCategory] = useState('');

  // Random motivational quote
  const quotes = [
    "Stay focused. Stay organized. Stay winning. üí™",
    "One task at a time. One step closer to success. üöÄ",
    "Your future is created by what you do today, not tomorrow. ‚ú®",
    "Small steps every day lead to big results. ‚úÖ",
    "Don‚Äôt watch the clock; do what it does. Keep going. ‚è≥"
  ];
  const randomQuote = quotes[Math.floor(Math.random() * quotes.length)];

  const fetchTasks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/tasks');
      setTasks(res.data);
    } catch (err) {
      console.error("Error fetching tasks:", err.message);
    }
  };

  const addTask = async () => {
    if (title.trim() && description.trim() && dueDate.trim() && category.trim()) {
      try {
        const res = await axios.post('http://localhost:5000/api/tasks', { title, description, dueDate, category });
        setTasks([...tasks, res.data]);
        setTitle('');
        setDescription('');
        setDueDate('');
        setCategory('');
      } catch (err) {
        console.error("Error adding task:", err.message);
      }
    }
  };

  const deleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/tasks/${id}`);
      setTasks(tasks.filter(task => task._id !== id));
    } catch (err) {
      console.error("Error deleting task:", err.message);
    }
  };

  const toggleTask = async (id, completed) => {
    try {
      const res = await axios.put(`http://localhost:5000/api/tasks/${id}`, { completed: !completed });
      setTasks(tasks.map(t => (t._id === id ? res.data : t)));
    } catch (err) {
      console.error("Error updating task:", err.message);
    }
  };

  const startEdit = (id, title, description, dueDate, category) => {
    setTasks(tasks.map(task =>
      task._id === id ? { ...task, isEditing: true, editTitle: title, editDescription: description, editDueDate: dueDate, editCategory: category } : task
    ));
  };

  const saveEdit = async (id) => {
    const taskToUpdate = tasks.find(t => t._id === id);
    try {
      const res = await axios.put(`http://localhost:5000/api/tasks/${id}`, {
        title: taskToUpdate.editTitle,
        description: taskToUpdate.editDescription,
        dueDate: taskToUpdate.editDueDate,
        category: taskToUpdate.editCategory,
      });
      setTasks(tasks.map(t =>
        t._id === id ? { ...res.data, isEditing: false } : t
      ));
    } catch (err) {
      console.error("Error editing task:", err.message);
    }
  };

  const filteredTasks = tasks.filter((task) => {
    const titleMatch = task?.title?.toLowerCase().includes(searchQuery.toLowerCase());
    const descriptionMatch = task?.description?.toLowerCase().includes(searchQuery.toLowerCase());
    const categoryMatch = filterCategory ? task.category === filterCategory : true;
    return (titleMatch || descriptionMatch) && categoryMatch;
  });

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div className="p-4 max-w-xl mx-auto relative min-h-screen">
      {/* Animated Background */}
      <div className="absolute top-0 left-0 w-full h-full overflow-hidden -z-10">
        <div className="absolute w-72 h-72 bg-blue-200 rounded-full mix-blend-multiply filter blur-2xl opacity-50 animate-float" style={{ top: '-5rem', left: '-5rem' }}></div>
        <div className="absolute w-72 h-72 bg-purple-200 rounded-full mix-blend-multiply filter blur-2xl opacity-50 animate-float-delay" style={{ bottom: '-5rem', right: '-5rem' }}></div>
      </div>

      <h1 className="text-3xl font-bold mb-2 text-center">üìù Task Mate</h1>
      <p className="text-center text-sm text-gray-600 italic mb-6">{randomQuote}</p>

      {/* Search */}
      <div className="mb-4">
        <input
          type="text"
          placeholder="Search tasks..."
          className="border p-2 w-full"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>

      {/* Filter */}
      <div className="mb-4">
        <select
          className="border p-2 w-full"
          value={filterCategory}
          onChange={(e) => setFilterCategory(e.target.value)}
        >
          <option value="">All Categories</option>
          <option value="Work">Work</option>
          <option value="Personal">Personal</option>
          <option value="Shopping">Shopping</option>
        </select>
      </div>

      {/* Add Task */}
      <div className="flex flex-wrap gap-2 mb-4">
        <input type="text" placeholder="Title" className="border p-2 flex-1" value={title} onChange={(e) => setTitle(e.target.value)} />
        <input type="text" placeholder="Description" className="border p-2 flex-1" value={description} onChange={(e) => setDescription(e.target.value)} />
        <input type="date" className="border p-2" value={dueDate} onChange={(e) => setDueDate(e.target.value)} />
        <select className="border p-2" value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="">Category</option>
          <option value="Work">Work</option>
          <option value="Personal">Personal</option>
          <option value="Shopping">Shopping</option>
        </select>
        <button onClick={addTask} className="bg-blue-500 text-white px-4 py-2 rounded">Add</button>
      </div>

      {/* Task List */}
      <ul>
        {filteredTasks.map((task) => (
          <li key={task._id} className="flex justify-between items-center border-b py-2">
            <div className="flex items-center gap-2">
              <input type="checkbox" checked={task.completed} onChange={() => toggleTask(task._id, task.completed)} />
              {task.isEditing ? (
                <div className="flex flex-wrap gap-2">
                  <input type="text" className="border px-2 py-1" value={task.editTitle} onChange={(e) => setTasks(tasks.map(t => t._id === task._id ? { ...t, editTitle: e.target.value } : t))} />
                  <input type="text" className="border px-2 py-1" value={task.editDescription} onChange={(e) => setTasks(tasks.map(t => t._id === task._id ? { ...t, editDescription: e.target.value } : t))} />
                  <input type="date" className="border px-2 py-1" value={task.editDueDate} onChange={(e) => setTasks(tasks.map(t => t._id === task._id ? { ...t, editDueDate: e.target.value } : t))} />
                  <select className="border px-2 py-1" value={task.editCategory} onChange={(e) => setTasks(tasks.map(t => t._id === task._id ? { ...t, editCategory: e.target.value } : t))}>
                    <option value="Work">Work</option>
                    <option value="Personal">Personal</option>
                    <option value="Shopping">Shopping</option>
                  </select>
                </div>
              ) : (
                <div>
                  <p className={task.completed ? "line-through text-gray-500" : "font-semibold"}>{task.title}</p>
                  <p className="text-sm text-gray-500">{task.description} | {task.dueDate} | {task.category}</p>
                </div>
              )}
            </div>
            <div className="flex items-center gap-2">
              {task.isEditing ? (
                <button onClick={() => saveEdit(task._id)} className="text-green-500">‚úÖ</button>
              ) : (
                <button onClick={() => startEdit(task._id, task.title, task.description, task.dueDate, task.category)} className="text-yellow-500">‚úè</button>
              )}
              <button onClick={() => deleteTask(task._id)} className="text-red-500 text-xl">üóë</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
